INSERT INTO `base_template`(`template_id`, `content`, `name`, `is_default`, `code`) VALUES ('wte3yelzpy', '<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"100%\" style=\"border-collapse:collapse;background-color:#F8F8F8;\" class=\"ke-zeroborder\">\n	<tbody>\n		<tr>\n			<td>\n				&nbsp;\n			</td>\n		</tr>\n		<tr>\n			<td>\n				<table align=\"center\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"800\" style=\"border:1px solid #f0f0f0;border-bottom:none;\" class=\"ke-zeroborder\">\n					<tbody>\n						<tr>\n							<td align=\"center\" bgcolor=\"#70bbd9\" style=\"padding:10px 20px;color:#ffffff;font-size:28px;font-weight:bold;font-family:Arial, sans-serif;text-align:left;\">\n								{companyName}\n							</td>\n						</tr>\n					</tbody>\n				</table>\n				<table align=\"center\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"800\" style=\"border:1px solid #f0f0f0;border-top:none;\" class=\"ke-zeroborder\">\n					<tbody>\n						<tr>\n							<td bgcolor=\"#ffffff\" style=\"padding:40px 30px 40px 30px;\">\n								<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"100%\" class=\"ke-zeroborder\">\n									<tbody>\n										<tr>\n											<td style=\"color:#153643;font-family:Arial, sans-serif;font-size:18px;\">\n												尊敬的用户，您好：\n											</td>\n										</tr>\n										<tr>\n											<td style=\"padding:20px 0 30px 0;color:#153643;font-family:Arial, sans-serif;font-size:14px;line-height:20px;padding-left:35px;\">\n												{messageBody}\n											</td>\n										</tr>\n										<tr>\n											<td style=\"color:#153643;font-size:18px;text-align:right;\">\n												{companyName}\n											</td>\n										</tr>\n									</tbody>\n								</table>\n							</td>\n						</tr>\n					</tbody>\n				</table>\n			</td>\n		</tr>\n		<tr>\n			<td>\n				&nbsp;\n			</td>\n		</tr>\n	</tbody>\n</table>', '邮件消息模板', 1, '000');
INSERT INTO `base_template`(`template_id`, `content`, `name`, `is_default`, `code`) VALUES ('wk6gsvpuy7', '<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"100%\" style=\"border-collapse:collapse;background-color:#F8F8F8;\" class=\"ke-zeroborder\">\n	<tbody>\n		<tr>\n			<td>\n				&nbsp;\n			</td>\n		</tr>\n		<tr>\n			<td>\n				<table align=\"center\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"800\" style=\"border:1px solid #f0f0f0;border-bottom:none;\" class=\"ke-zeroborder\">\n					<tbody>\n						<tr>\n							<td align=\"center\" bgcolor=\"#70bbd9\" style=\"padding:10px 20px;color:#ffffff;font-size:28px;font-weight:bold;font-family:Arial, sans-serif;text-align:left;\">\n								{companyName}\n							</td>\n						</tr>\n					</tbody>\n				</table>\n				<table align=\"center\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"800\" style=\"border:1px solid #f0f0f0;border-top:none;\" class=\"ke-zeroborder\">\n					<tbody>\n						<tr>\n							<td bgcolor=\"#ffffff\" style=\"padding:40px 30px 40px 30px;\">\n								<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"100%\" class=\"ke-zeroborder\">\n									<tbody>\n										<tr>\n											<td style=\"color:#153643;font-family:Arial, sans-serif;font-size:18px;\">\n												尊敬的用户，您好：\n											</td>\n										</tr>\n										<tr>\n											<td style=\"padding:20px 0 30px 0;color:#153643;font-family:Arial, sans-serif;font-size:14px;line-height:20px;padding-left:35px;\">\n												你此次注册的验证码是：<b>{registryCode}</b>，请在 {expireTime} 分钟内输入验证码进行下一步操作。 <br />\n如非你本人操作，请忽略此邮件。\n											</td>\n										</tr>\n										<tr>\n											<td style=\"color:#153643;font-size:18px;text-align:right;\">\n												{companyName}\n											</td>\n										</tr>\n									</tbody>\n								</table>\n							</td>\n						</tr>\n					</tbody>\n				</table>\n			</td>\n		</tr>\n		<tr>\n			<td>\n				&nbsp;\n			</td>\n		</tr>\n	</tbody>\n</table>', '注册验证码', 1, '001');
INSERT INTO `base_template`(`template_id`, `content`, `name`, `is_default`, `code`) VALUES ('wk6gsvpuy8', '<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"100%\" style=\"border-collapse:collapse;background-color:#F8F8F8;\" class=\"ke-zeroborder\">\n	<tbody>\n		<tr>\n			<td>\n				&nbsp;\n			</td>\n		</tr>\n		<tr>\n			<td>\n				<table align=\"center\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"800\" style=\"border:1px solid #f0f0f0;border-bottom:none;\" class=\"ke-zeroborder\">\n					<tbody>\n						<tr>\n							<td align=\"center\" bgcolor=\"#70bbd9\" style=\"padding:10px 20px;color:#ffffff;font-size:28px;font-weight:bold;font-family:Arial, sans-serif;text-align:left;\">\n								{companyName}\n							</td>\n						</tr>\n					</tbody>\n				</table>\n				<table align=\"center\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"800\" style=\"border:1px solid #f0f0f0;border-top:none;\" class=\"ke-zeroborder\">\n					<tbody>\n						<tr>\n							<td bgcolor=\"#ffffff\" style=\"padding:40px 30px 40px 30px;\">\n								<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"100%\" class=\"ke-zeroborder\">\n									<tbody>\n										<tr>\n											<td style=\"color:#153643;font-family:Arial, sans-serif;font-size:18px;\">\n												尊敬的用户，您好：\n											</td>\n										</tr>\n										<tr>\n											<td style=\"padding:20px 0 30px 0;color:#153643;font-family:Arial, sans-serif;font-size:14px;line-height:20px;padding-left:35px;\">\n												你此次重置密码的验证码是：<b>{registryCode}</b>，请在 {expireTime} 分钟内输入验证码进行下一步操作。 <br />\n如非你本人操作，请忽略此邮件。\n											</td>\n										</tr>\n										<tr>\n											<td style=\"color:#153643;font-size:18px;text-align:right;\">\n												{companyName}\n											</td>\n										</tr>\n									</tbody>\n								</table>\n							</td>\n						</tr>\n					</tbody>\n				</table>\n			</td>\n		</tr>\n		<tr>\n			<td>\n				&nbsp;\n			</td>\n		</tr>\n	</tbody>\n</table>', '重置密码验证码', 1, '003');
